# 方法1
class Solution:
    def exist(self, board, word: str) -> bool:
        init = [[0 for i in range(len(board[0]))] for j in range(len(board))]
        for i in range(len(board)):
            for j in range(len(board[0])):
                if self.func(init, i, j, board, word) is True:
                    return True
        return False

    def func(self, init, i, j, board, word):
        if word == "" or (len(word) == 1 and init[i][j] == 0 and board[i][j] == word[0]):
            return True
        elif board[i][j] != word[0] or init[i][j] == 1:
            return False
        else:
            t, b, l, r = False, False, False, False
            init[i][j] = 1
            if i - 1 >= 0:
                if self.func(init, i - 1, j, board, word[1:]):
                    return True
            if i + 1 <= len(board) - 1:
                if self.func(init, i + 1, j, board, word[1:]):
                    return True
            if j - 1 >= 0:
                if self.func(init, i, j - 1, board, word[1:]):
                    return True
            if j + 1 <= len(board[0]) - 1:
                if self.func(init, i, j + 1, board, word[1:]):
                    return True
            init[i][j] = 0
            return False


# 方法2
class Solution:
    def exist(self, board, word: str) -> bool:
        def func(i, j, word):
            if len(word) == 1 and board[i][j] == word[0]:
                return True
            if board[i][j] != word[0]:
                return False
            temp = board[i][j]
            board[i][j] = '0'
            if i - 1 >= 0:
                # 这判断成功了，直接return True，就不用进行后面了，否则超时
                if func(i - 1, j, word[1:]):
                    return True
            if i + 1 < len(board):
                if func(i + 1, j, word[1:]):
                    return True
            if j - 1 >= 0:
                if func(i, j - 1, word[1:]):
                    return True
            if j + 1 < len(board[0]):
                if func(i, j + 1, word[1:]):
                    return True
            board[i][j] = temp

        for i in range(len(board)):
            for j in range(len(board[0])):
                if func(i, j, word):
                    return True
        return False


